  cursor:none;
  ::-moz-selection {
    color:#fff;
    background: rgb(233, 138, 21);
  }

  ::selection {
    color:#fff;
    background: rgb(233, 138, 21);
  }
}

#cursor {
  z-index: 1000;
  position: absolute;
  background-color: #fff;
  width:6px;
  height:6px;
  border-radius: 50%;
  pointer-events: none;
  transform: translate(-50%,-50%);
  @media (max-width:800px) {
    display:none;
  }
}
#cursor-follower{
  z-index: 1000;
  position: absolute;
  border: solid 2px #fff;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  pointer-events: none;
  transform: translate(-50%,-50%);
  transition:
          height .2s ease-in,
          width .2s ease-in,
          background .2s ease-in,
          opacity .2s ease-in;
  ;
  &.follower-hover{
    opacity: .5;
    background-color: #fff;
    width: 60px;
    height: 60px;
  }
  @media (max-width:800px) {
    display:none;
  }
}

.container{
  width:100vw;
  height:100vh;
  overflow: hidden;
  position: relative;
  background: rgba(0, 52, 49, 0.7);
  &::after{
    z-index: 0;
    content:'';
    position: absolute;
    top:0;
    left:0;
    width: 100%;
    height: 100%;
    background: url('assets/noise.gif');
    opacity: 0.1;
  }
}

.navigation{
  font-family: 'Roboto', sans-serif;
  color:#fff;

  &__link{
    position:absolute;
    font-size: 14px;
    line-height: 16px;
    letter-spacing: 0.22em;
    font-weight: bold;
    z-index: 200;
    @media (min-width:800px) {
      font-size: 18px;
      line-height: 21px;
    }

    &--logo{
      top:12%;
      left:10%;
    }

    &--github{
      bottom:13%;
      left:10%;
    }

    &--cv{
      bottom:13%;
      right:10%;
    }
  }
}

.burger{
  position: absolute;
  top:12%;
  right:10%;
  z-index: 200;
  @media (min-width:800px) {
    display:none;
  }

  &__line{
    display: inline-block;
    width:4px;
    height:32px;
    background-color: #fff;
    margin-right:10px;

    &:last-child{
      margin-right: 0;
    }
  }
}

.burger-menu{
  position: absolute;
  top:0;
  left:0;
  width: 100vw;
  height:100vh;
  background: rgba(233, 138, 21);
  z-index: 100;
  transform: translateX(100%);
  @media (min-width:800px){
    display:none;
  }

  &::after{
    z-index: 0;
    content:'';
    position: absolute;
    top:0;
    left:0;
    width: 100%;
    height: 100%;
    background: url('assets/noise.gif');
    opacity: 0.1;
  }

  &__link{
    position: absolute;
    top:30%;
    left:10%;
    font-style: normal;
    font-weight: 900;
    font-size: 30px;
    &:nth-child(2){
      top:40%;
    }
  }
}

.navigation__desktop{
  display:none;
  position: absolute;
  top:12%;
  right:10%;
  text-align: right;

  @media (min-width:800px) {
    .navigation__link{
      position: unset;
      margin-bottom: 17px;
      &:last-child{
        margin:0;
      }
    }
    display:flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-end;
    white-space: nowrap;
  }
}

