      }
    }

    &__image{
      width:100%;
      height:auto;
      filter: grayscale(1);
    }
  }
  .slide-count{
    position:absolute;
    bottom:23%;
    right:10%;
    color:#fff;
    font-weight: 700;
    letter-spacing: 0.22em;

    @media (min-width:800px) {
      left:26%;
      top:69%
    }

    &__current-slide{
      font-size: 36px;
    }

    &__no-slides{
      font-size: 18px;
    }
  }
